C++<pre><code>#include&lt;stdio.h&gt;
#include&lt;stdlib.h&gt;
#include&lt;string.h&gt;
typedef int Status;
const int FALSE = 0;
const int TRUE = 1;
//学生信息数据类型定义
typedef struct{
char name[10];
char stu_number[10];
char phone[10];
}ElemType;
//链表结点定义
typedef struct Node{
ElemType data;      //数据域
struct Node *next;  //指针域
}Lnode,*LinkList;
//1.初始化链表
Status initialization(LinkList &amp;Head)
{
Head=(LinkList)malloc(sizeof(Lnode));
//开辟空间失败，返回FALSE
if(Head==NULL)
{
return FALSE;
}
Head-&gt;next=NULL;
return TRUE;
}
//2.创建(链表赋值)
Status CreateList(LinkList &amp;Head)
{
if(Head==NULL)
{
return FALSE;
}
int n;
LinkList p;
printf("请输入你要存储的学生人数:\n");
scanf("%d",&amp;n);
for(int i = 0; i &lt; n; i++ )
{
p=(LinkList)malloc(sizeof(Lnode));
printf("请输入第%d个学生的姓名、学号和电话号码\n",i+1);
scanf("%s%s%s",p-&gt;data.name,p-&gt;data.stu_number,p-&gt;data.phone);
p-&gt;next=Head-&gt;next;
Head-&gt;next=p;
}
return TRUE;
}
//3.输出
void Display(LinkList Head)
{
LinkList p;
int i=1;
p=Head-&gt;next;
printf("序号\t姓名\t学号\t\t电话\n");
while(p!=NULL)
{
printf("%d\t%s\t%s\t%s\n",i,p-&gt;data.name,p-&gt;data.stu_number,p-&gt;data.phone);
p=p-&gt;next;
++i;
}
}
//4.销毁
Status Destroy(LinkList &amp;Head)
{
LinkList p;
if(Head==NULL)
{
return FALSE;
}
while(Head!=NULL)
{
p=Head;
Head=Head-&gt;next;
free(p);
}
return TRUE;
}
//5.插入
Status Insert(LinkList &amp;Head)
{
if(Head==NULL)
{
return FALSE;
}
LinkList p;
p=(LinkList)malloc(sizeof(Lnode));
if(p==NULL)
{
return FALSE;
}
printf("请输入你要插入的学生信息：\n");
scanf("%s%s%s",p-&gt;data.name,p-&gt;data.stu_number,p-&gt;data.phone);
p-&gt;next=Head-&gt;next;
Head-&gt;next=p;
return TRUE;
}
//6.根据姓名查找
Status SearchName(LinkList Head)
{
if(Head==NULL)
{
return FALSE;
}
char name[10];
printf("请输入要查找的学生姓名：\n");
scanf("%s",name);
LinkList p;
p=Head-&gt;next;
while(strcmp(p-&gt;data.name,name)!=0)
{
p=p-&gt;next;
}
printf("查找的学生信息：\n");
printf("姓名：%s\t学号：%s\t电话：%s\n",p-&gt;data.name,p-&gt;data.stu_number,p-&gt;data.phone);
return TRUE;
}
//7.删除
Status Delete(LinkList &amp;Head,ElemType &amp;e)
{
if(Head==NULL)
{
return FALSE;
}
int j,i;
printf("请输入要删除的序号：\n");
scanf("%d",&amp;j);
int y=0;
printf("是否删除(1/0)：");
scanf("%d",&amp;y);
if(y==1)
{
LinkList p=Head;
for(i=0;i&lt;j-1;++i)
{
if(p!=NULL)
{
p=p-&gt;next;
}
}
//q为待删除结点
LinkList q=p-&gt;next;
p-&gt;next=q-&gt;next;
//保存删除结点的内容
e=q-&gt;data;
free(q);
printf("删除的信息:%s\t%s\t%s\n",e.name,e.stu_number,e.phone);
return TRUE;
}
else
{
printf("已退出删除！\n");
return FALSE;
}
}
void fun()
{
system("pause");
system("cls");
}
//菜单
void MenuList()
{
printf("\n"
"*********************************************************\n"
"*\t\t1、初始化    \t\t\t\t*\n"
"*\t\t2、赋值\t\t\t\t\t*\n"
"*\t\t3、输出\t\t\t\t\t*\n"
"*\t\t4、销毁\t\t\t\t\t*\n"
"*\t\t5、插入\t\t\t\t\t*\n"
"*\t\t6、查找\t\t\t\t\t*\n"
"*\t\t7、删除\t\t\t\t\t*\n"
"*\t\t0、退出\t\t\t\t\t*\n"
"*********************************************************\n"
);
}
int main()
{
LinkList node;
int x;
ElemType e;
while(1)
{
MenuList();
printf("请输入要执行的功能：");
int n=0;
scanf("%d",&amp;n);
switch (n) {
case 0:{
exit(1);
fun();
break;
}
case 1:{
x=initialization(node);
printf("初始化：%d\n\n",x);
fun();
break;
}
case 2:{
x=CreateList(node);
printf("链表赋值：%d\n\n",x);
fun();
break;
}
case 3:{
Display(node);
fun();
break;
}
case 4:{
x=Destroy(node);
printf("销毁：%d\n\n",x);
fun();
break;
}
case 5:{
x=Insert(node);
printf("插入：%d\n\n",x);
fun();
break;
}
case 6:{
x=SearchName(node);
printf("\n查找：%d\n\n",x);
fun();
break;
}
case 7:{
x=Delete(node,e);
printf("删除：%d\n\n",x);
fun();
}
}
}
return 0;
}
</code></pre>

C++<pre><code>#include&lt;stdio.h&gt;
#include&lt;stdlib.h&gt;
#include&lt;string.h&gt;
const int MAX = 45;
typedef int Status;
const int TRUE = 1;
const int FALSE = 0;
//学生信息数据类型定义
typedef struct
{
char name[10];
char num[10];
long long int telephone;
}Student;
//顺序表定义
typedef struct
{
Student *infor;
int length;
}Sqlist,*SqList;
void fun()
{
system("pause");
system("cls");
}
//初始化
Status InitList(SqList &amp;list)
{
list = (SqList)malloc(sizeof(Sqlist));
if(!list)
{
return FALSE;
}
list-&gt;infor = (Student*)malloc(MAX * sizeof(Student));
if(list-&gt;infor ==NULL)
{
return FALSE;
}
list-&gt;length=0;
return TRUE;
}
//赋值
void CreatList(SqList &amp;list,Student stu[],int length)
{
for(int i=0;i&lt;length;i++)
{
list-&gt;infor[i]=stu[i];
}
list-&gt;length=length;
}
//删除
Status DeleteList(SqList &amp;list)
{
char str[10];
printf("请输入你要删除的学生姓名：\n");
scanf("%s",str);
int i=0;
while(strcmp(list-&gt;infor[i].name,str)!=0)
{
i++;
}
for(int j=i;j&lt;=list-&gt;length;j++)
{
//后移
list-&gt;infor[j]=list-&gt;infor[j+1];
}
list-&gt;length--;
fun();
return TRUE;
}
//插入
Status InsertList(SqList &amp;list,Student e)
