typedef int ElemType;
typedef int Status;
//链式队列的类型定义
typedef struct Qnode{
ElemType data;     //数据域
struct Qnode *next;//指针域
}QNode,*QuenePtr;
typedef struct{
QuenePtr front;//队头指针
QuenePtr rear; //队尾指针
}LinkQueue;
//1.链队列初始化
Status InitQueue(LinkQueue &amp;Q)
{
Q.front=Q.rear=(QuenePtr)malloc(sizeof(Qnode));
if(Q.front==NULL||Q.rear==NULL)
{
return FALSE;
}
//头结点指针域置空
Q.front-&gt;next=NULL;
return TRUE;
}
//2.销毁
Status DestroyQueue(LinkQueue &amp;Q)
{
if(Q.front==NULL)
{
return FALSE;
}
while(Q.front!=NULL)
{
//创建一个新的指针p,指向首元结点下一个结点的地址
QNode *p=Q.front-&gt;next;
//释放队头指针
free(Q.front);
//将首元结点的下一个结点的地址赋给Q.front使其成为新的首元结点
Q.front=p;
/*Q.rear=Q.front-&gt;next;free(Q.front);Q.front=Q.rear;*/
}
return TRUE;
}
//3.入队
Status EnQueue(LinkQueue &amp;Q,ElemType e)
{
QNode *p=(QNode*)malloc(sizeof(QNode));
if(p==NULL)
{
return FALSE;
}
//将元素e的值赋给新结点的数据域
p-&gt;data=e;
//将新结点的指针域空置
p-&gt;next=NULL;
//将队尾指针中的指针域指向结点p
//这里Q.rear-&gt;next可理解为结点an的指针域，新结点p为结点an+1
Q.rear-&gt;next=p;
//队尾指针指向结点p
Q.rear=p;
return TRUE;
}
//4.出队
Status DeQueue(LinkQueue &amp;Q,ElemType &amp;e)
{
//判断队空
if(Q.front==Q.rear)
{
return FALSE;
}
//创建一个指针变量，使其指向要删除的结点的地址
QNode *p;
p=Q.front-&gt;next;
//将删除结点的数据域赋给变量e
e=p-&gt;data;
//将头结点与删除结点后面的结点相连
Q.front-&gt;next=p-&gt;next;
//当要释放的结点为队尾结点时，需修改队尾指针和队头指针
if(Q.rear==p)
{
Q.front=Q.rear;
}
free(p);
return TRUE;
}
//5.求对头元素
Status GetHead(LinkQueue Q,ElemType &amp;e)
{
if(Q.front==Q.rear)
{
return FALSE;
}
e=Q.front-&gt;next-&gt;data;
return TRUE;
}
//6.遍历
void dispaly(LinkQueue Q)
{
if(Q.front==Q.rear)
return;
QNode *p=Q.front-&gt;next;
while(p!=NULL)
{
printf("%d ",p-&gt;data);
p=p-&gt;next;
}
printf("\n");
printf("****\n\n");
}
