typedef struct StackNode{
ElemType data;
struct StackNode *next;
}StackNode,*LinkStack;
//1.链栈初始化
LinkStack CreateStack()
{
LinkStack S;
S=(LinkStack)malloc(sizeof(StackNode));
if(S==NULL)
{
return NULL;
}
S-&gt;next=NULL;//头结点指针域空置
return S;
}
//2.判断是否为空
Status IsEmpty(LinkStack S)
{
if(S-&gt;next==NULL)
return TRUE;
else return FALSE;
}
//3.入栈（使用头插法）
void Push(ElemType e,LinkStack S)
{
//创建一个新结点
StackNode *TmpCell;
TmpCell=(StackNode *)malloc(sizeof(StackNode));
//将元素e的值赋给新结点的数据域
TmpCell-&gt;data=e;
//将头结点后面的地址赋给新结点的指针域
TmpCell-&gt;next=S-&gt;next;
//再将头结点的指针域指向新结点
S-&gt;next=TmpCell;
}
//4.出栈
Status Pop(LinkStack S,ElemType *e)
{
StackNode *FirstCell;
if(IsEmpty(S))//判空函数
{   printf("栈为空\n");
return FALSE;
}
else
{
//FirstCell指向首元结点（第一个结点）
FirstCell=S-&gt;next;
S-&gt;next=FirstCell-&gt;next;
*e=FirstCell-&gt;data;
free(FirstCell);
return TRUE;
}
}
//5.求栈顶元素
ElemType GetTop(LinkStack S)
{
return (S-&gt;next-&gt;data);
}
//6.链栈长度
int GetLength(LinkStack S)
{
StackNode *p;
p=S-&gt;next;
int sum=0;
while(p)
{
sum++;
p=p-&gt;next;
}
return sum;
}
//7.遍历
void dispaly(LinkStack S)
{
if(S==NULL)
{
return;
}
StackNode *p=S-&gt;next;
while(p!=NULL)
{
printf("%d ", p-&gt;data);
p = p-&gt;next;
}
printf("\n");
printf("*****\n");
}
int main()
{
//初始化
LinkStack S=CreateStack();
int Y=0;
//判空
Y=IsEmpty(S);
printf("%d\n",Y);
//入栈
int arry[5]={1,2,3,4,5};
for(int i=0;i&lt;5;i++)
{
Push(arry[i],S);
}
dispaly(S);
//栈顶元素
Y=GetTop(S);
printf("Stack_Top=%d\n",Y);
//判空
Y=IsEmpty(S);
printf("%d\n",Y);
//出栈
Pop(S,&amp;Y);
dispaly(S);
