C<pre><code>#include&lt;stdio.h&gt;
#include&lt;stdlib.h&gt;
const int MAX = 100;
const int TRUE = 1;
const int FALSE = 0;
typedef int ElemType;
typedef int Status;
struct stack
{
ElemType* base;//栈底指针
ElemType* top; //栈顶指针
int stacksize; //栈可用最大容量
};
typedef struct stack SqStack;//栈的定义
//结构体无类型名定义
/*
  typedef struct {
  ElemType* base;
  ElemType* top;
  int size;
  }SqStack;
 */
//1.顺序栈的初始化
Status InitStack(SqStack* S)
{
//开辟栈的最大空间,S-&gt;base这个指针指向这个空间的首元素
S-&gt;base = (ElemType*)malloc(MAX*sizeof(ElemType));
if (!(S-&gt;base))  //非0的话，就分配成功。为0的话，分配失败
{
return FALSE;
}
S-&gt;top = S-&gt;base;//栈顶指针等于栈底指针--&gt;空栈
S-&gt;stacksize = MAX;
return TRUE;
}
//2.判断栈是否为空
Status StackEmpty(SqStack S)
{
if (S.top == S.base)
{
return TRUE;
}
else
{
return FALSE;
}
}
//3.求顺序栈长度
int StackLength(SqStack S)
{
int length = 0;
length = S.top - S.base;
return length;
}
//4.清空顺序栈
Status ClearStack(SqStack* S)
{
if (S-&gt;base)//(S-&gt;base)!=0,栈存在的话
{
S-&gt;top = S-&gt;base;
}
return TRUE;
}
//5.销毁顺序栈
Status DestroyStack(SqStack* S)
{
if (S-&gt;base)
{
free(S-&gt;base);//将栈底指针释放
S-&gt;stacksize = 0;
S-&gt;base = S-&gt;top = NULL;
return TRUE;
}
else return FALSE;
}
//6.入栈
Status Push(SqStack* S, ElemType e)
{
//栈满
if (S-&gt;top - S-&gt;base == S-&gt;stacksize)
{
return FALSE;
}
//将数组中的值赋给（S-&gt;top）地址里面的空间
*(S-&gt;top) = e;
(S-&gt;top)++;
return TRUE;
}
//7.出栈
Status Pop(SqStack* S, ElemType* e)
{
if (S-&gt;top == S-&gt;base)//栈空
{
return FALSE;
}
--(S-&gt;top);    //栈顶指针下移（S-&gt;top指针中的空间为空）
*e = *(S-&gt;top);//删除的栈顶赋值给e
return TRUE;
}
//8.顺序栈的遍历
Status display(SqStack S)
{
if (S.base)//栈存在
{
//在顺序栈中S.top指针永远指向入栈后的下一个元素的地址（*S.top）为空;先下移一个元素
--(S.top);
while (S.top &gt;= S.base)
{
printf("%d ", *(S.top));
--(S.top);
}
printf("***");
printf("\n");
return TRUE;
}
else return FALSE;
}
int main()
{
Status Y = 0;
ElemType arry[5] = { 2,3,1,9,8 };
SqStack head;
Y=InitStack(&amp;head);        //顺序栈初始化
printf("Y=%d\n",Y);
Y=StackEmpty(head);        //是否为空
printf("是否为空:%d\n", Y);
Y=StackLength(head);       //栈长
printf("栈长:%d\n", Y);
for (int i = 0; i &lt; 5; i++)
{
Y=Push(&amp;head, arry[i]);//入栈
}
printf("入栈:%d\n", Y);
display(head);
Pop(&amp;head, &amp;Y);            //出栈
printf("Y=%d\n", Y);
display(head);
printf("栈顶元素：%d 栈的最大存储元素：%d\n", *(head.top--), head.stacksize);
ClearStack(&amp;head);         //清空
Y = StackLength(head);     //栈长
printf("栈长:%d\n", Y);
display(head);
DestroyStack(&amp;head);       //销毁
Y = StackEmpty(head);      //是否为空
printf("是否为空:%d\n", Y);
return 0;
}
</code></pre>

C<pre><code>#include&lt;stdio.h&gt;
#include&lt;stdlib.h&gt;
typedef int dataType;
typedef int Status;
const int TRUE = 1;
const int FALSE = 0;
// sequence list定义
typedef struct{
dataType *data;
int length;
}SqList;
// 1. 初始化list
SqList initList(dataType data[], int length){
SqList sq;
sq.data = data;
sq.length = length;
return sq;
}
// 2. 判断表是否为空
Status isEmpty(SqList sq){
if(sq.length == 0){
return TRUE;
}else{
return FALSE;
}
}
// 3. 销毁表
Status clearList(SqList* sq){
sq-&gt;data = NULL;
sq-&gt;length = 0;
return TRUE;
}
// 4. 增 -- &gt; 插入 位置按逻辑下标
Status insertElemBaseOfIndex(SqList *sq,int index, dataType elem){
if(index &lt; 1 || index &gt; sq-&gt;length){
return FALSE;
}
dataType t;
for(int i = sq-&gt;length-1; i&gt;=index; i--){
t = sq-&gt;data[i];
sq-&gt;data[i] = sq-&gt;data[i+1];
sq-&gt;data[i+1] = t;
}
sq-&gt;data[index] = elem;
sq-&gt;length += 1;
return TRUE;
}
// 5. 删
Status deleteElemBaseOfIndex(SqList* sq,int index,dataType* e){
if(index &lt; 1 || index &gt; sq-&gt;length){
return FALSE;
}
*e = sq-&gt;data[index-1];
dataType t;
for(int i=index-1;i&lt;sq-&gt;length;i++){
t = sq-&gt;data[i];
sq-&gt;data[i] = sq-&gt;data[i+1];
sq-&gt;data[i+1] = t;
}
sq-&gt;length--;
return TRUE;
}
// 6. 改
Status updateElem(SqList* sq, int index, dataType e){
if(index &lt; 1 || index &gt; sq-&gt;length){
return FALSE;
}
sq-&gt;data[index-1] = e;
return TRUE;
}
// 7. 查
Status searchElem(SqList* sq,dataType e){
for(int i=0;i&lt;sq-&gt;length;i++){
if(sq-&gt;data[i]==e){
return i+1;
}
}
return -1;
}
// 8. 遍历
void displayList(SqList sq){
for(int i=0;i&lt;sq.length;i++){
printf("%d ",sq.data[i]);
}
printf("\n");
}
int main(){
system("chcp 65001");
dataType arrs[] = {5,6,4,1,3};
