C<pre><code>#include&lt;stdlib.h&gt;
#include&lt;stdio.h&gt;
const int TRUE = 1;
const int FALSE = 0;
const int MAX=100;
typedef int ElemType;
typedef int Status;
//顺序队列数据类型定义
typedef struct{
ElemType *base;//指向首元素地址
int front;     //队头元素下标
int rear;      //队尾元素下标
}SqQueue;
//1.队列初始化
Status InitQueue(SqQueue &amp;Q)
{
//分配数组空间
Q.base=(ElemType*)malloc(sizeof(ElemType)*MAX);
//判断分配空间是否成功
if(Q.base==NULL)
{
return FALSE;
}
Q.front=Q.rear=0;
return TRUE;
}
//2.求队列长度
int QueueLength(SqQueue Q)
{
//当Q.rear-Q.front为负数时
return ((Q.rear-Q.front+MAX)%MAX);
}
//3.入队
Status EeQueue(SqQueue &amp;Q,ElemType e)
{
//判断队列是否已满
if((Q.rear+1)%MAX==Q.front)
{
return FALSE;
}
//新元素加入队尾
Q.base[Q.rear]=e;
//队尾下标加1
//当Q.rear=MAX时回到0，达到循环的实现
Q.rear=(Q.rear+1)%MAX;
return TRUE;
}
//4.出队
Status DeQueue(SqQueue &amp;Q,ElemType &amp;e)
{
//判断队列是否为空
if(Q.front==Q.rear)
{
return FALSE;
}
//保存对头元素于e
e=Q.base[Q.front];
//对头下标加1
Q.front=(Q.front+1)%MAX;
return TRUE;
}
//5.取对头元素
ElemType GetHead(SqQueue Q)
{
//判断队列不为空
if(Q.front!=Q.rear)
{
//返回对头元素的值
return Q.base[Q.front];
}
else
{
return FALSE;
}
}
//6.遍历
void dispaly(SqQueue Q)
{
//判断是否为空
if(Q.front==Q.rear)
{
return;
}
else
{
while(Q.front!=Q.rear)
{
printf("%d ",Q.base[Q.front]);
//当Q.front%MAX==0时，从头开始循环
Q.front=(Q.front+1)%MAX;
}
printf("\n");
printf("*****\n");
}
}
int main()
{
int arry[5]={1,2,3,4,5};
int x=0;
SqQueue Q;
//初始化
x=InitQueue(Q);
printf("初始化：%d\n",x);
//队列长度
x=QueueLength(Q);
printf("Length:%d\n",x);
//入队
for(int i=0;i&lt;5;i++)
{
EeQueue(Q,arry[i]);
}
dispaly(Q);
//取对头元素
x=GetHead(Q);
printf("Head==%d\n",x);
//出队
DeQueue(Q,x);
dispaly(Q);
printf("x==%d\n",x);
//取对头元素
x=GetHead(Q);
printf("Head==%d\n",x);
//队列长度
x=QueueLength(Q);
printf("Length:%d\n",x);
return 0;
}
